	private static boolean legalFilename(String filename){
		if(filename == null)
			return false;
		else if(filename.trim() == "")
			return false;
		else if(filename.startsWith("."))
			return false;
		else if(!(filename.toLowerCase().endsWith(".jpg") || filename.toLowerCase().endsWith(".jpeg") || filename.toLowerCase().endsWith(".jpe")))
			return false;
		return filename.matches("^[^<>:\"/\\\\|?*\\x00-\\x1F]*[^<>:\"/\\\\|?*\\x00-\\x1F\\ .]$");
	}
	
	public static void returnImage(ServletResponse response, String imageFileName)  {
		logger.debug("imerpihab2: returning image: " + imageFileName);
		try {
			response.setContentType("image/jpeg");			
			String pathToWeb = slideShowDir;
			if(!pathToWeb.endsWith(File.separator))
				pathToWeb += File.separator;
			logger.debug("imperihab2: imageFileName: " + imageFileName);
			if(!legalFilename(imageFileName))
				return;

			logger.debug("imperihab2: imageFileName full: " + pathToWeb + imageFileName);
			File f = new File(pathToWeb + imageFileName);
			FileInputStream input = new FileInputStream(f);
			FileChannel channel = input.getChannel();
			byte[] buffer = new byte[256 * 1024];
			ByteBuffer byteBuffer = ByteBuffer.wrap(buffer);
			OutputStream out = response.getOutputStream();
			try {
			    for (int length = 0; (length = channel.read(byteBuffer)) != -1;) {
			        out.write(buffer, 0, length);
			        byteBuffer.clear();
			    }
			} finally {
			    input.close();
			}
			out.close();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			logger.debug("imperihab2: error: " + e.getMessage());
			e.printStackTrace();
			
		}
	}
	
