	public static void writeHtml(PrintWriter writer){
		String photosHtml = "<script type='text/javascript'>var photos = [";
		
		try{
			File folder = new File(slideShowDir);		
			if(folder.exists()){
				File[] listOfFiles = folder.listFiles();
				for(File file : listOfFiles){
					if(!legalFilename(file.getName()))
						continue;
					photosHtml += "\"ss-image/" +file.getName() +"\",";
				}
				if(photosHtml.endsWith(","))
					photosHtml = photosHtml.substring(0, photosHtml.length() - 1);
			}
		}catch(Exception ex){
		
		}
		
		photosHtml += "];\n</script>\n";
		
		writer.write(htmlStart1);
		writer.write(KeypadAway);
		writer.write(htmlStart2);
		writer.write(KeypadDisarmed);
		writer.write(htmlStart3);
		writer.write(KeypadHome);
		writer.write(htmlStart4);
		writer.write(Loading);		
		writer.write(htmlStart5);
		writer.write(KeypadIntrusionDetected);		
		writer.write(htmlStart6);
		writer.write(Siren);		
		writer.write(htmlStart7);
		writer.write(ButtonArmAway);
		writer.write(htmlStart11);
		writer.write(ButtonArmHome);
		writer.write(htmlStart12);
		writer.write(LoadingLarge);
		writer.write(htmlStart14);
		writer.write(ArmingCancel);
		writer.write(htmlStart15);
				
		writer.write(photosHtml);
		

		writer.write("<script type='text/javascript'>\n");
		writer.write(MicroAjax);
		writer.write("</script>\n");
		writer.write("<script type='text/javascript'>\n");
		writer.write(AlarmJS);
		writer.write("</script>\n");
		//writer.write("<script type='text/javascript' src='http://alfred/website/alarm2.js'></script>\n");
		writer.write("</body></html>");

	}

